package com.just_me.news.news

import com.just_me.news.utils.MCrypt
import org.junit.Test

import org.junit.Assert.*

/**
 * Example local unit test, which will execute on the development machine (host).
 *
 * See [testing documentation](http://d.android.com/tools/testing).
 */
class MCryptTest {

    @Test
    fun seeInside() {
        val encrypted = ""
        val mcrypt = MCrypt()
        val string = String(mcrypt.decrypt(encrypted))
        assert(string.length > 20)
    }

    @Test
    fun toTheMoonAndBack() {
        val data = "To the moon and back"
        val mcrypt = MCrypt()
        var encrypted = MCrypt.bytesToHex(mcrypt.encrypt(data))
        val decrepted = String(mcrypt.decrypt(encrypted))
        assertEquals(data, decrepted)
    }

}
